function getStartTime(st,tz)
{var date=parseDate(st);var now=new Date();var utc=now.getTime()+(now.getTimezoneOffset()*60000);var nd=new Date(utc+(3600000*(parseInt(tz)+getDST())));var dateDiff=(date-nd);if(dateDiff<=0){return'00:00:00:00';}else{var days=Math.floor(dateDiff/(1000*60*60*24));var r=dateDiff-(days*1000*60*60*24);var hours=Math.floor(r/(1000*60*60));r=r-(hours*1000*60*60);var minutes=Math.floor(r/(1000*60));r=r-(minutes*1000*60);var seconds=Math.floor(r/1000);return(fixZero(days)+':'+
fixZero(hours)+':'+
fixZero(minutes)+':'+
fixZero(seconds));}}
function parseDate(st)
{var d=st.split(" ")[0];var t=st.split(" ")[1];var date=new Date(d.split("/")[2],d.split("/")[1]-1,d.split("/")[0],t.split(":")[0],t.split(":")[1],t.split(":")[2]);return date;}
function fixZero(x)
{if(x.toString().length==1){return'0'+x.toString();}else{return x.toString();}}
function getDST()
{var today=new Date();var yr=today.getFullYear();var jan=new Date(yr,0);var jul=new Date(yr,6);if(jan.getTimezoneOffset()>jul.getTimezoneOffset()&&today.getTimezoneOffset()!=jan.getTimezoneOffset()){return 1;}
if(jan.getTimezoneOffset()<jul.getTimezoneOffset()&&today.getTimezoneOffset()!=jul.getTimezoneOffset()){return 1;}
return 0;}